import React, { useContext } from "react";
import {
  ImageBackground,
  Platform,
  StyleSheet,
  Text,
  View,
  Image,
  FlatList,
  TouchableOpacity,
} from "react-native";

import { FlatGrid } from 'react-native-super-grid';


import { Card, ListItem, Icon } from 'react-native-elements'

import colors from "../../assets/constants/colors";
import Button from "../../assets/Button";



const LevelSelection = ({ numPlayers, onNumPlayersChange, startGame }) => {
  
  const [items, setItems] = React.useState([
    { name: 'TURQUOISE', code: '#1abc9c', state:'visible' },
    { name: 'EMERALD', code: '#2ecc71', state:'visible'  },
    { name: 'PETER RIVER', code: '#3498db', state:'visible'  },
    { name: 'AMETHYST', code: '#9b59b6', state:'visible'  },
    { name: 'WET ASPHALT', code: '#34495e', state:'visible'  },
    { name: 'GREEN SEA', code: '#16a085', state:'visible'  },
    { name: 'NEPHRITIS', code: '#27ae60', state:'visible'  },
    { name: 'BELIZE HOLE', code: '#2980b9', state:'visible'  },
    { name: 'WISTERIA', code: '#8e44ad', state:'visible'  },
    { name: 'MIDNIGHT BLUE', code: '#2c3e50', state:'visible'  },
    { name: 'SUN FLOWER', code: '#f1c40f', state:'visible'  },
    { name: 'CARROT', code: '#e67e22', state:'visible'  },
    
  ]);

  return (
    <View style={styles.root}>
      <ImageBackground source={require("../../assets/trianglify.png")} style={styles.root}>
      
      <FlatGrid
      itemDimension={130}
      data={items}
      style={styles.gridView}
      // staticDimension={300}
      // fixed
      spacing={10}
      renderItem={({ item }) => (
        <View style={[styles.itemContainer, { backgroundColor: item.code }]}>
          <Text style={styles.itemName}>{item.name}</Text>
          <Text style={styles.itemCode}>{item.code}</Text>
        </View>
      )}
    />

  
      
      
        
          <Text style={styles.text}>Please select the level:</Text>
            
          <Button
            
            onPress={startGame}
            style={{ marginTop: 24 },styles.button}
          >
            {/* <Image source={require("../../assets/background/background_lvl2.png")}></Image> */}
            <Text style={styles.button_text}>START GAME</Text>
          </Button>

         
      </ImageBackground>
    </View>

  );
};

const styles = StyleSheet.create({
  root: {
    ...StyleSheet.absoluteFillObject,
    justifyContent: "center",
    alignItems: "center",
    
  },
  button: {
    marginBottom: 8,
    width: '30vw'
  },
  button_text:{
    fontSize: '2vw',
  },
  active: {
    borderColor: "blue",
  },
  text: {
    color: colors.grayDark,
    fontFamily: "Dimbo",
    fontSize: '3vw',
    lineHeight: 60,
    marginBottom: 32,
  },
});

export default LevelSelection;
