import React, { useContext } from "react";
import {
  ImageBackground,
  Platform,
  StyleSheet,
  Text,
  View,
} from "react-native";

import colors from "../../assets/constants/colors";
import Button from "../../assets/Button";

const LevelSelection = ({ numPlayers, onNumPlayersChange, startGame }) => {
  return (
    <View style={styles.root}>
      <ImageBackground source={require("../../assets/trianglify.png")} style={styles.root}>
        
          <Text style={styles.text}>Please select the level:</Text>
         

          <Button
            color={colors.yellowLight}
            onPress={startGame}
            style={{ marginTop: 24 },styles.button}
          >
            <ImageBackground source={require("../../assets/background/background_lvl2.png")}></ImageBackground>
            <Text style={styles.button_text}>START GAME</Text>
          </Button>

         
      </ImageBackground>
    </View>

  );
};

const styles = StyleSheet.create({
  root: {
    ...StyleSheet.absoluteFillObject,
    justifyContent: "center",
    alignItems: "center",
    
  },
  button: {
    marginBottom: 8,
    width: '30vw'
  },
  button_text:{
    fontSize: '2vw',
  },
  active: {
    borderColor: "blue",
  },
  text: {
    color: colors.grayDark,
    fontFamily: "Dimbo",
    fontSize: '3vw',
    lineHeight: 60,
    marginBottom: 32,
  },
});

export default LevelSelection;
